    static displayName = AdminPage.name;

    constructor(props) {
        super(props);
        this.state = { 
            to: undefined,
            subject: undefined,
            content: undefined,
        };
        this.adminApi = new AdminApi();

    }
    
    
    handleInputChange(event){
        const target = event.target;
        this.setState({
            [target.name]: target.value
        });
    }
    
