                <Grid item xs>
                <Button href="/Register" variant="contained">Close Store </Button>
                </Grid>
                <Grid item xs={3}>
                <Button href="/Register" variant="contained">Remove User </Button>
                </Grid>
                <Grid item xs>
                <Button href="/Register" variant="contained">User Message </Button>
                </Grid>
                <Grid item xs>
                <Button href="/Register" variant="contained">User Purchase History </Button>
                </Grid>
                <Grid item xs>
                <Button href="/Register" variant="contained">Purchase History </Button>
                </Grid>
                <Grid item xs>
                <Button onClick={()=>this.get_market_stats} variant="contained">Statistics </Button>
                </Grid>
                </Grid>

                <Box sx={{ flexGrow: 3 }}>

                </Box>
                    <h3> </h3>
